mapper.select:


    SELECT * FROM (
        SELECT rownum no, m.* from (  
           SELECT * FROM FAMILYEVENT
			ORDER BY familyeventNo DESC
           ) m
        ) WHERE no between ${startNo} and ${endNo}

mapper.update:

update FAMILYEVENT 
set familyeventHit = familyeventHit + 1 
where familyeventNo = #{_parameter}
